import React, {useState} from 'react';
import './styles.css';
import {Link, useHistory} from 'react-router-dom';
import {FiArrowLeft} from 'react-icons/fi';

import api from '../services/api';

import logoImg from '../../assets/logo.png';

import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';

import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';



export default function Register() {

    const classes = useStyles();
    const [age, setAge] = React.useState('');
  
    const handleChange = (event) => {
      setAge(event.target.value);
    };


    const [name,setName]=useState('');
    const [email,setEmail]=useState('');
    const [whatsapp,setWhatsapp]=useState('');
    const [city,setCity]=useState('');
    const [uf,setUF]=useState('');
    const [grupo,setgrupo]=useState('');
    
    const [centrolojistico,setcentrolojistico]=useState('');

    const history = useHistory();

    async function handleRegister(e) {
        e.preventDefault();
        const data = {name,email,whatsapp,city,uf,grupo,centrolojistico};

        try{
        const response =  await api.post('ongs', data);

        alert(`Seu ID de acesso: ${response.data.id} `);
        history.push('/');
        }catch(err){
            alert('Erro no cadastro, tente novamente.')
        }

    }

    return (
        <div className="register-container">
            <div className="content">
            <section>
            <img src={logoImg} alt="Cadastro Simples"/>

            <h1>Cadastro</h1>
            <p>Faça seu cadastro, entre na pataforma e ajude pessoas a encontrarem os casos da sua ONG.</p>
            <Link className="back-link" to  = "/"><FiArrowLeft size={16} color="#E02041"/>Tenho cadastro </Link> 
            </section>

            <form onSubmit={handleRegister}>
            <input placeholder="Nome da ONG" value={name} onChange={e=>setName(e.target.value)}></input>
            <input type="email" placeholder="E-mail(Não obrigatório)" value={email} onChange={e=>setEmail(e.target.value)}></input>
            <input placeholder="Whatsapp" value={whatsapp} onChange={e=>setWhatsapp(e.target.value)}></input>
            <div className="input-group">
            <input placeholder="Cidade" value={city} onChange={e=>setCity(e.target.value)}></input>
            <input placeholder="UF" style={{width:80 }} value={uf} onChange={e=>setUF(e.target.value)}></input>
            </div>

             <div>
             <FormControl className={classes.formControl}>
        <InputLabel id="demo-simple-select-label">Age</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={age}
          onChange={handleChange}
        >
          <MenuItem value={10}>Ten</MenuItem>
          <MenuItem value={20}>Twenty</MenuItem>
          <MenuItem value={30}>Thirty</MenuItem>
        </Select>
      </FormControl>
             </div>

            <input placeholder="Grupo de negocio" value={grupo} onChange={e=>setgrupo(e.target.value)}></input>
            <input placeholder="CentroLogistico" value={centrolojistico} onChange={e=>setcentrolojistico(e.target.value)}></input>
            <button className="button" type="submit">Cadastrar</button>

            </form>

            </div>
        </div>
    )
}
