import React, { useState } from 'react';
import {Link, useHistory} from 'react-router-dom'
import {FiArrowLeft} from 'react-icons/fi';

import api from '../services/api';

import './styles.css';
import logoImg from '../../assets/logo.png';

import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
//import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const useStyles = makeStyles((theme) => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
      
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
  }));


export default function NewIncident(){
    
    const classes = useStyles();


    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [value, setValue] = useState('');
    const ongId = localStorage.getItem('ongId');
    const history = useHistory();
    const [instagram, setInstagram] = useState('');
    const [destaque, setDestaque] = useState('');


    async  function handleNewIncident(e) {
        e.preventDefault();

        const data = {
            title,
            description,
            value,
            instagram,
            destaque 
        };

        try {
          await api.post('incidents', data, {
              headers: {
                  Authorization: ongId,
              }
          })
          history.push('/profile');  
        }catch(err) {
            alert ('Erro ao cadastrar caso, tente novamente');
        }
    }

     
    return (
        <div className="new-incident-container">
            <div className="content">
            <section>
            

            <h1>Cadastarnovocaso</h1>
            <p>Descreva o caso detalhadamente para enconrar um heroi para resolver isso.</p>
            <Link className="back-link" to  = "/profile"><FiArrowLeft size={16} color="#E02041"/>Voltar para a home </Link> 
            </section>

            <form onSubmit={handleNewIncident}> 
            <input 
                placeholder="Nome da empresa"
                value={title}
                onChange={e => setTitle(e.target.value)}>    
                </input>
            <textarea 
                placeholder="Descrição do instagram "
                value={description}
                onChange={e => setDescription(e.target.value)}>
                </textarea>
            <input 
                placeholder="URL do Instagram"
                value={value}
                onChange={e => setValue(e.target.value)}>
                </input>
            <input 
                placeholder="Link da foto do Instagram" 
                value={instagram} 
                onChange={e => setInstagram(e.target.value)}>
                </input>

                <FormControl className={classes.formControl} >
        <InputLabel  id="demo-simple-select-label">Destacar na pagina principal?</InputLabel>
        <Select style={{width:120 }}
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={destaque}
          onChange={e => setDestaque(e.target.value)}
        >
          <MenuItem value={'1'}>Não</MenuItem>
          <MenuItem value={'2'}>Sim</MenuItem>
          
         
        </Select>
      </FormControl>
            <input 
                placeholder="Destacar na pagina principal?" 
                value={destaque} 
                onChange={e => setDestaque(e.target.value)}>
                </input>
            
            <button className="button" type="submit">Cadastrar</button>

            </form>

            </div>
        </div>
    )
}
