import React, { useState } from 'react';
import {Link, useHistory} from 'react-router-dom'
import {FiArrowLeft} from 'react-icons/fi';

import api from '../services/api';

import './styles.css';
import logoImg from '../../assets/logo.png';

export default function NewIncident(){
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [value, setValue] = useState('');
    const ongId = localStorage.getItem('ongId');
    const history = useHistory();
    const [instagram, setInstagram] = useState('');
    const [destaque, setDestaque] = useState('');


    async  function handleNewIncident(e) {
        e.preventDefault();

        const data = {
            title,
            description,
            value,
            instagram,
            destaque 
        };

        try {
          await api.post('incidents', data, {
              headers: {
                  Authorization: ongId,
              }
          })
          history.push('/profile');  
        }catch(err) {
            alert ('Erro ao cadastrar caso, tente novamente');
        }
    }

     
    return (
        <div className="new-incident-container">
            <div className="content">
            <section>
            

            <h1>Cadastarnovocaso</h1>
            <p>Descreva o caso detalhadamente para enconrar um heroi para resolver isso.</p>
            <Link className="back-link" to  = "/profile"><FiArrowLeft size={16} color="#E02041"/>Voltar para a home </Link> 
            </section>

            <form onSubmit={handleNewIncident}> 
            <input 
                placeholder="Nome da empresa"
                value={title}
                onChange={e => setTitle(e.target.value)}>    
                </input>
            <textarea 
                placeholder="Descrição do instagram "
                value={description}
                onChange={e => setDescription(e.target.value)}>
                </textarea>
            <input 
                placeholder="URL do Instagram"
                value={value}
                onChange={e => setValue(e.target.value)}>
                </input>
            <input 
                placeholder="Instagram Foto" 
                value={instagram} 
                onChange={e => setInstagram(e.target.value)}>
                </input>
            <input 
                placeholder="Destaque" 
                value={destaque} 
                onChange={e => setDestaque(e.target.value)}>
                </input>
            
            <button className="button" type="submit">Cadastrar</button>

            </form>

            </div>
        </div>
    )
}
